INSERT INTO articles (id,author_id,title,announce,full_text,date_create) VALUES
 	(1,1,'Как достигнуть успеха не вставая с кресла','Помните, небольшое количество ежедневных упражнений лучше, чем один раз, но много.','Программировать не настолько сложно, как об этом говорят. Игры и программирование разные вещи.Не стоит идти в программисты, если вам нравится только игры. Первая большая ёлка была установлена только в 1938 году. Процессор заслуживает особого внимания.Он обязательно понравится геймерам со стажем. Собрать камни бесконечности легко, если вы прирожденный герой. Простые ежедневные упражнения помогут достичь успеха. Он написал больше 30 хитов. Вы можете достичь всего.Стоит только немного постараться и запастись книгами. Освоить вёрстку несложно.Возьмите книгу новую книгу и закрепите все упражнения на практике. Ёлки — это не просто красивое дерево.Это прочная древесина.','2020.05.20. 07:56:42'),
	(2,1,'Самый лучший музыкальный альбом этого года','Золотое сечение — соотношение двух величин, гармоническая пропорция. Программировать не настолько сложно, как об этом говорят. Помните, небольшое количество ежедневных упражнений лучше, чем один раз, но много. Как начать действовать ? Для начала просто соберитесь.','Вы можете достичь всего.Стоит только немного постараться и запастись книгами. Ёлки — это не просто красивое дерево.Это прочная древесина. Рок - музыка всегда ассоциировалась с протестами.Так ли это на самом деле ? Бороться с прокрастинацией несложно.Просто действуйте.Маленькими шагами. Как начать действовать ? Для начала просто соберитесь. Он написал больше 30 хитов. Из под его пера вышло 8 платиновых альбомов. Это один из лучших рок - музыкантов. Достичь успеха помогут ежедневные повторения. Программировать не настолько сложно, как об этом говорят.','2020.06.02. 12:47:11'),
